  gearChart() {
    const data = {
      labels: ["#0AD11E", "Yellow","Red" ],
      datasets: [{
        label: '# of Votes',
        data: [12, 19, 3],
        backgroundColor: ["#0AD11E", "#E1E600","#D90000"],
        
      }]
    };

    // Configuration options for the gauge chart
    const options = {
      rotation: 270, // start angle in degrees
      circumference: 180,
      cutout: '80%', // Adjust the cutout value to control the size of the gauge
      scales: {
        x: { display: false },
        y: { display: false },
      },
      plugins: {
        legend: {
          display: false,
       },
       tooltip: {
          enabled:false
       },
       datalabels: {
        // font: {
        //   weight: 'bold',
        //   size: 15,
          
        // },
        color: 'white'
      },
       afterDraw: (chart:any) => {
        var needleValue = chart.config.data.datasets[0].needleValue;
        var dataTotal = chart.config.data.datasets[0].data.reduce((a:any, b:any) => a + b, 0);
        var angle = Math.PI + (1 / dataTotal * needleValue * Math.PI);
        var ctx = chart.ctx;
        var cw = chart.canvas.offsetWidth;
        var ch = chart.canvas.offsetHeight;
        var cx = cw / 2;
        var cy = ch - 6;
  
        ctx.translate(cx, cy);
        ctx.rotate(angle);
        ctx.beginPath();
        ctx.moveTo(0, -3);
        ctx.lineTo(ch - 20, 0);
        ctx.lineTo(0, 3);
        ctx.fillStyle = 'rgb(0, 0, 0)';
        ctx.fill();
        ctx.rotate(-angle);
        ctx.translate(-cx, -cy);
        ctx.beginPath();
        ctx.arc(cx, cy, 5, 0, Math.PI * 2);
        ctx.fill();
      } 
      },
    };
    

    // Get the chart canvas element
    const gaugeNeedal={
      id:'gaugeNeedal',
      afterDatasetsDraw(chart:any, args:any, plugins:any){
        const {ctx, data}= chart;
        ctx.save();
        const xCenter = chart.getDatasetMeta(0).data[0].x;
        const yCenter = chart.getDatasetMeta(0).data[0].y;
        const outRadius = chart.getDatasetMeta(0).data[0].outerRadius;
        const innerRadius = chart.getDatasetMeta(0).data[0].innerRadius;

        const widthSlice =(outRadius - innerRadius)/2;
        const radius =15;
        const angle = Math.PI /180;

        ctx.translate(xCenter,yCenter);
        ctx.beginPath();
        ctx.strokeStyle='black';
        ctx.fillStyle='gray';
        ctx.lineWidth=1;
        ctx.moveTo(0-15,0);
        ctx.lineTo(0,0-innerRadius);
        ctx.lineTo(0+15,0);
        ctx.closePath();
        ctx.stroke();
        ctx.fill();
      

        //dot 
        ctx.beginPath();
        ctx.arc(0,0,radius,0,angle*360,false);
        ctx.fill();
        ctx.restore();
      }
    };
    // const gaugeFlowMeter ={
    //   id : 'gaugeFlowMeter',
    //   afterDatasetsDraw(chart:any, args:any, plugins:any){
    //     const {ctx, data}= chart;
    //     ctx.save();
    //   }
    // }
    // Create the gauge chart
    
    const gaugeChart = new Chart('gearChart', {
      type: 'doughnut',
      data: data,
      options: options,
      plugins: [gaugeNeedal]
  });
  
  }

